import React, { useState } from 'react'
import { StyleSheet, Text, View, Image, Button, Alert } from 'react-native'
import { Root, Popup } from 'popup-ui'
import * as ImagePicker from 'expo-image-picker'
import * as Permissions from 'expo-permissions'

export default function App() {
  const [isHide, setIsHide] = useState(true);
  const [isFuku, setIsFuku] = useState(false);

	const askForPermission = async () => {
		const permissionResult = await Permissions.askAsync(Permissions.CAMERA)
		if (permissionResult.status !== 'granted') {
			alert('no permissions to access camera!', [{ text: 'ok' }])
			return false
		}
		return true
  }
  const fukuAlert = () => {
    alert('There is Fukushima(福島)', [{ text: 'Ok' }]);
  }
  const notFukuAlert = () => {
    alert('There is not Fukushima(福島)', [{ text: 'Ok' }]);
  }

	takeImage = async () => {
    setIsHide(false)
		// make sure that we have the permission
		const hasPermission = await askForPermission()
		if (!hasPermission) {
			return
		} else {
			// launch the camera with the following settings
			let image = await ImagePicker.launchCameraAsync({
				mediaTypes: ImagePicker.MediaTypeOptions.Images,
				allowsEditing: true,
				aspect: [3, 3],
				quality: 1,
				base64: true,
			})
			// make sure a image was taken:
			if (!image.cancelled) {
				const response = await fetch('http://52.185.172.211:5000/', {
					method: 'POST',
					headers: {
						Accept: 'application/json',
						'Content-Type': 'application/json',
					},
					// send our base64 string as POST request
					body: JSON.stringify({
						imgsource: image.base64,
					}),
        })
        const result = await response.text()
        console.log("===test")
        console.log(result)
        const parsed = result.replace(/[\n]+/g, '');

        console.log("===test2")
        console.log(parsed)

        if (parsed == "True") {
          console.log(">>> true condition")
          alert('There is Fukushima(福島)!!', [{ text: 'Ok' }]);
          setIsFuku(true)
        }else {
          console.log(">>> false condition")
          // alert('There is no Fukushima(福島)!!', [{ text: 'Ok' }]);
          setIsHide(false)
          Popup.show({
            type: 'Success',
            title: 'No Fukushima(福島) on picture',
            button: false,
            textBody: 'Congrats! You can buy it',
            buttonText: 'Ok',
            callback: () => Popup.hide()
          })
        }
			}
		}
	}
	return (
		<View style={styles.container}>
      {/* { {isHide} ? null : {isFuku} ? fukuAlert() : notFukuAlert()} */}
			<Button title="Take a photo" onPress={takeImage} />

		</View>
	)
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#fff',
		alignItems: 'center',
		justifyContent: 'center',
	},
})
